para.screen:
# Set debug level
debug	= 0;
#
# Enable hot plugging
hotplug	= yes;
#
# Path to ifdhandler
ifdhandler = /usr/sbin/ifdhandler;
#
# Statically configure non-hotplug aware readers here
reader towitoko {
	driver = towitoko;
	device = /dev/ttyS1;
};

#
# Hotplug IDs
driver	etoken {
	ids = {
		usb:0529/050c,
	};
};
driver	egate {
	ids = {
		usb:0973/0001,
	};
};
